def snap():
    # Handle image: resize, BW etc
    img_name = datetime.now().strftime("%Y-%m-%d %H:%M:%S")
    
    webcam = cv2.VideoCapture(0) 

    check, img = webcam.read()

    # resize to fit minitel's dimensions
    dim = (80,60) #*60 to keep aspect ratio and maybe add some text
    img_small = cv2.resize(img, dim)

    imagePath = 'images/' + img_name + '.jpg'
    cv2.imwrite(filename=imagePath, img=img) # Original

    webcam.release()
    
    display_snap(img_small) # Start to display image while dithering is processed

    if (w_fs == "y"):
        img = fs_dither(img)
    else:
        img = cv2.cvtColor(img, cv2.COLOR_BGR2GRAY)
        img = cv2.resize(img, (print_width, print_height))

    img = img_footer(img)

    cv2.imwrite(filename='images/'+img_name+'_dth.jpg', img=img)

    return dither_ready(img, imagePath)

###
# Append footer
# img = opencv Array
# return opencv Array
###
def img_footer(img):
    # Append footer / watermark
    img = Image.fromarray(img) # From opencv Array to PIL Image
    footer = Image.open('resources/footer.jpg')

    ## concatenate footer
    img_new = Image.new('RGB', (img.width, img.height + footer.height))
    img_new = img_new.convert('L')
    img_new.paste(img, (0, 0))
    img_new.paste(footer, (0, img.height))
    #img_new.save('images/testfooter.jpg')
    img.close()
    footer.close()

    return np.array(img_new) # return opencv Array

### 
# Dither image with Floyd-Steinberg algorithm.
# Source: https://gist.github.com/bzamecnik/33e10b13aae34358c16d1b6c69e89b01
### 
def fs_dither(img):
    print("Dithering in process...")

    # To BW
    img = cv2.cvtColor(img, cv2.COLOR_BGR2GRAY) # To be dithered for printing later
    # Resize
    img = cv2.resize(img, (print_width, print_height))
    
    #input is supposed as color
    # grab the image dimensions
    h = img.shape[0]
    w = img.shape[1]
    # loop over the image
    for y in range(0, h-1):
        for x in range(1, w-1):
            # threshold the pixel
            old_p = img[y, x]
            new_p = np.round(old_p/255.0) * 255
            img[y, x] = new_p
            
            quant_error_p = old_p - new_p

            img[y, x+1] = minmax(img[y, x+1] + quant_error_p * 0.4375) # 7/16
            img[y+1, x-1] = minmax(img[y+1, x-1] + quant_error_p * 0.1875) # 3 / 16.0
            img[y+1, x] = minmax(img[y+1, x] + quant_error_p * 0.3125) # 5 / 16.0
            img[y+1, x+1] = minmax(img[y+1, x+1] + quant_error_p * 0.0625) # 1 / 16.0

    # return the thresholded image
    return img

def minmax(v):
    if v > 255:
        v = 255
    if v < 0:
        v = 0
    return v

