def to_printer(img, imagePath):
    print("printing...")
    minitel.position(4,1)
    minitel.efface('ligne')
    minitel.envoyer('Printing, please wait...')
    minitel.position(5,1)
    minitel.efface('ligne')
    
    try:
        with  open('/dev/usb/lp0', "wb") as printer:
            try:
                printer.write(bytearray(b"\x1b\x40")) ## ESC @ // initialise printer

                img = Image.fromarray(img) # From opencv Array to PIL Image

                img_w, img_h = img.size
                wh = img_w>>8
                wl = img_w & 255

                for i in range (0,5):
                    printer.write(bytearray(b"\x1b\x33\x18")) ## ESC 3 24 // line spacing

                printer.write(bytearray(b"\x1b\x50")) ## ESC P // 10CPI
                printer.write(bytearray(b"\x1b\x6c\x12")) ## ESC l 10 // 1 inch left margin

                for i in range (0,20):
                    printer.write(bytearray(b"\x0a\x0d")) ## \n\r

                for y in range(0, img_h-8, 8):
                    printer.write(bytearray(b"\x0a\x0d")) ## \n\r
                    printer.write(bytearray(b"\x1b\x33\x18")) ## ESC 3 24 // reset line spacing each line, just to be sure
                    printer.write(bytearray(b"\x1b\x2a\x05")) ## ESC * 5 wl wh // Bit image graphics mode
                    printer.write(wl.to_bytes(1, 'little'))
                    printer.write(wh.to_bytes(1, 'little'))
                    print(y)
                    for x in range(0, img_w):
                        b=0
                        if (img.getpixel((x, y)) == 0): b = b | 128
                        if (img.getpixel((x, y+1)) == 0): b = b | 64
                        if (img.getpixel((x, y+2)) == 0): b = b | 32
                        if (img.getpixel((x, y+3)) == 0): b = b | 16
                        if (img.getpixel((x, y+4)) == 0): b = b | 8
                        if (img.getpixel((x, y+5)) == 0): b = b | 4
                        if (img.getpixel((x, y+6)) == 0): b = b | 2
                        if (img.getpixel((x, y+7)) == 0): b = b | 1
                        printer.write(b.to_bytes(1, 'little'))

                printer.write(bytearray(b"\x0a\x0d")) ## \n\r
                printer.write(bytearray("\n\r"+TXT_FOOTER_1+"\n\r", encoding='utf8'))
                printer.write(bytearray(TXT_FOOTER_2, encoding='utf8'))
                printer.write(bytearray(b"\x0a\x0d")) ## \n\r

                for i in range (0,35):
                    printer.write(bytearray(b"\x0a\x0d")) ## \n\r

            except OSError as e:
                print('Error when printing')
                minitel.position(4,22)
                minitel.efface('ligne')
                minitel.envoyer('Error when printing...')
                print(e)
                
    except FileNotFoundError as e:
        print('ERROR!\nCan\'t seem to contact printer')

    printer.close()

    print("Ready")
    #time.sleep(17)   ## Needed time to print rest of the image
    
    #minitel.efface('ligne')
    #minitel.envoyer('Printing done! Have a nice day!')
    #minitel.bip()
    upload(imagePath)

    return splashscreen()

def share(response):
    qr = qrcode.QRCode(
        version=1,
        error_correction=qrcode.constants.ERROR_CORRECT_L,
        box_size=2,
        border=2,
    )
    qr.add_data(response)
    qr.make(fit=True)

    img = qr.make_image(fill_color="white", back_color=(0,0,1))
    print(img.size)

    minitel.efface('vraimenttout')
    image_minitel = ImageMinitel(minitel)
    image_minitel.importer(img)
    image_minitel.envoyer(5, 1)

    minitel.position(8,23)
    minitel.efface('ligne')
    minitel.taille(largeur = 2, hauteur = 2)
    minitel.envoyer('Scan & share!')

    print('displaying qcode')

    keyPress = minitel.recevoir(True, None)
    if(keyPress == " "):
        return splashscreen()

###
## Upload picture to backend
###
def upload(imagePath):
    print(imagePath)
    try:
        print("Sharing to api")
        files = {'file': open(imagePath, 'rb')}
        headers = {'Authorization': 'Bearer ' + API_BEARER}
        r = requests.put(API_URL, files=files, headers=headers)

    except:
        print("Error while uploading photo")

    jsonresponse = r.json()
    url = FRONT_URL + "/" + jsonresponse['uuid'][:7]
    share(url)

    return True



